function Slider() {
    $("#imageSlide").fadeOut("slow", function() {
        $(this).attr("src", slideshowImageURLs[slideshowImageURLs.length++ % slideshowImageCount]).fadeIn("slow");
    });
}

var slideshowImageURLs = [ "https://s3-ap-southeast-2.amazonaws.com/ajshawitbucket/RiseVison/The+most+dangerous+kind+of+waste+-+Shigeo+Shingo.jpg", "https://s3-ap-southeast-2.amazonaws.com/ajshawitbucket/RiseVison/You+add+value+to+people+when+you+value+them+-+John+Maxwell.jpg", "https://s3-ap-southeast-2.amazonaws.com/ajshawitbucket/RiseVison/underpromise+overdeliver.PNG", "https://s3-ap-southeast-2.amazonaws.com/ajshawitbucket/RiseVison/4+purposes+of+improvment.PNG", "https://s3-ap-southeast-2.amazonaws.com/ajshawitbucket/RiseVison/4TQ+Value.jpg", "https://s3-ap-southeast-2.amazonaws.com/ajshawitbucket/RiseVison/Listen+with+the+intent+to+understand.jpg" ], slideshowImageCount = slideshowImageURLs.length;

$(function() {
    $("#imageSlide").attr("src", $(slideshowImageURLs).get(-1));
}), $(function() {
    setInterval(Slider, 2e4);
});